import { EmbedBuilder } from "discord.js";
export default class GuesserModelEmebds {
    static results(data) {
        return new EmbedBuilder()
            .setAuthor({
            name: "Daily 3 - Guesser Model",
        })
            .setTitle("Guesser Model Analysis Results")
            .addFields({
            name: "Nominated Choices (Top 3)",
            value: `1. ${data.nominatedChoices[0].join("")}\n2. ${data.nominatedChoices[1].join("")}\n3. ${data.nominatedChoices[2].join("")}`,
            inline: false,
        }, {
            name: "Positional Statistics (Top 3)",
            value: "```\n" + data.byPositionReport + "```",
            inline: false,
        }, {
            name: "Sequential Statistics (Top 6)",
            value: "```\n" + data.bySequenceReport + "```",
            inline: false,
        }, {
            name: "Pattern Statistics (Top 6)",
            value: "```\n" + data.byPatternReport + "```",
            inline: false,
        }, {
            name: "Latest 10 Draws",
            value: "```\n" + data.latestTop10Draws + "```",
            inline: false,
        })
            .setColor("#f56e00")
            .setFooter({
            text: "Generated by Calottery Guesser Module",
        })
            .setTimestamp();
    }
}
